import React, { useState } from "react";
import Layout from "../core/Layout";
import { isAuthenticated } from "../auth";
import { Link } from "react-router-dom";
import { createCategory } from "./apiAdmin";

const AddCategory = () => {

    const [name, setName] = useState("");
    const [error, setError] = useState(false);
    const [success, setSuccess] = useState(false);

    // destructure user and token from localstorage
    const { user, token } = isAuthenticated();


    const newCategoryFom = () => (
      <form
      id="registrationForm"
      action="/"
      method="POST"
      onSubmit={(e) => submitData(e)}
    >
      <Container>
        <Row>
          <ToastContainer />
          <Col sm="6">

            <Form.Group as={Row} className="mb-3" controlId="formPlaintextname">
              <Col sm="12">
                <Form.Label>Enter Name</Form.Label>

                <Form.Control type="text"
                  name="clientName"
                  value={clientName.value}
                  placeholder="Enter Name"
                  onChange={(e) => setclientName({ ...clientName, value: e.target.value })}
                  onBlur={() => setclientName({ ...clientName, touched: true })}
                />

                {isError(clientName) && <p className='text-danger'>{clientName.error}</p>}

              </Col>
            </Form.Group>
          </Col>

        </Row>
      </Container>
      <hr />
      {/* <Button className='float-right'>Submit</Button> */}
      <Button className='float-end' type="submit" disabled={isSubmitting}>
        Submit
      </Button>
    </form>
    );



    const goBack = () => (
        <div className="mt-5">
            <Link to="/admin/dashboard" className="text-warning">
                Back to Dashboard
            </Link>
        </div>
    );

    return (
        <Layout
            title="Add a new category"
            description={`G'day ready to add a new category?`}
        >
            <div className="row">
                <div className="col-md-8 offset-md-2">
                    {/* {showSuccess()} */}
                    {/* {showError()} */}
                    {newCategoryFom()}
                    {goBack()}
                </div>
            </div>
        </Layout>
    );
};

export default AddCategory;
